DROP PROCEDURE IF EXISTS `publish_procedure`;
DELIMITER ;;
CREATE DEFINER=`root`@`192.168.1.101` PROCEDURE `publish_procedure`(gid bigint(8),period int,pubHisTable varchar(225),rctPubTable varchar(225),goodsTable varchar(225),buyHisTable_g varchar(225),baseCode int)
BEGIN SET @sqlpubhis = CONCAT('UPDATE ',pubHisTable,' pt ,goods.',goodsTable,' gt SET pt.lucky_code = ',baseCode,' +FLOOR(RAND()*gt.totalCount+1),pub_status = 1 WHERE pt.gid=gt.gid AND pub_status = 0 AND pt.gid=',gid,' AND pt.period = ',period); SET @sqlrecpub = CONCAT('UPDATE ',rctPubTable,' rt ,',pubHisTable,' pt SET rt.lucky_code = pt.lucky_code,rt.pub_status = 1 WHERE rt.gid=pt.gid AND rt.pub_status = 0 AND rt.gid=pt.gid AND rt.period = pt.period'); SET @sqlgetpid = CONCAT('UPDATE ',pubHisTable,' pt ,',buyHisTable_g, ' bt SET pt.pid = bt.pid WHERE pt.gid = bt.gid AND pt.period = bt.period AND pt.lucky_code = bt.code AND pt.gid = ',gid,' AND pt.period = ',period); SET @sqlbuyhisg = CONCAT('UPDATE ',buyHisTable_g,' SET pub_status = 1 where gid = ',gid, ' AND period = ',period); PREPARE stmtpubhis FROM @sqlpubhis; PREPARE stmtrecpub FROM @sqlrecpub; PREPARE stmtgetpid FROM @sqlgetpid; PREPARE stmtbuyhisg FROM @sqlbuyhisg; EXECUTE stmtpubhis; EXECUTE stmtrecpub; EXECUTE stmtgetpid; EXECUTE stmtbuyhisg; DEALLOCATE PREPARE stmtpubhis; DEALLOCATE PREPARE stmtrecpub; DEALLOCATE PREPARE stmtgetpid; DEALLOCATE PREPARE stmtbuyhisg; END
;;
DELIMITER ;

